import React, { Component } from 'react';
import axios from 'axios';
import { send } from 'q';
import { request } from 'https';
import { formatError } from 'graphql';

export class Login extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			username: '',
			password: '',
			checkbox: false,
			errors: {
				Username: '',
				password: '',
				usernameError: '',
				passwordError: ''
			}
		};
		this.handlePasswordChange = this.handlePasswordChange.bind(this);
		this.handleUserChange = this.handleUserChange.bind(this);
		this.handleCheckedChange = this.handleCheckedChange.bind(this);
		this.handleSubmit = this.handleSubmit.bind(this);
	}
	handleUserChange(e) {
		this.setState({ username: e.target.value });
		console.log(this.state.username);
	}
	handlePasswordChange(e) {
		this.setState({ password: e.target.value });
		console.log(this.state.password);
	}
	handleChecboxChange(e) {
		this.setState({ checkbox: e.target.value });
		console.log(this.state.checkbox);
	}
	handleSubmit(e) {
		e.preventDefault();
		const isValid = this.validate();
		if (isValid) {
			console.log(this.state);
		}
	}

	validate = () => {
		let usernameError = '';
		//	let passwordError= '';
		if (this.state.username.includes('.', ',', '@')) {
			usernameError = 'invaalid eusername';
		}
		if (usernameError) {
			this.state({ usernameError });
			return false;
		}
		return true;
	};
	signin() {
		axios
			.post('/', {
				username: this.state.username,
				passsword: this.state.password
			})
			.then(
				response => {
					console.log(response);
				},
				error => {
					console.log(error);
				}
			);
	}

	render() {
		return (
			<div className='body-container'>
				<h2 className='login-form'>Sing In</h2>
				<div className='content'>
					<div className='form'>
						<form onSubmit={e => this.handleSubmit(e)} method='post'>
							<div className='form-group'>
								<label htmlFor='username-field'>Username</label>
								<input
									type='text'
									name='username'
									className='form-control'
									id='username-field'
									value={this.state.username}
									placeholder='username'
									onChange={e => this.handleUserChange(e)}
								/>
							</div>
							{this.state.username ? (
								<div style={{ color: 'red' }}>{this.state.usernameError}</div>
							) : null}
							<div className='form-group'>
								<label htmlFor='password-field'>Password</label>
								<input
									type='password'
									name='password'
									id='password-field'
									value={this.state.password}
									className='form-control'
									placeholder='password'
									onChange={e => this.handlePasswordChange(e)}
								/>
							</div>
							<div className='form-group'>
								<label htmlFor='checkbox-field'>rememder me?</label>
								<input
									type='checkbox'
									name='password'
									id='checkbox-field'
									value={this.state.checkbox}
									className='form-control'
									placeholder='password'
									onChange={e => this.handlePasswordChange(e)}
								/>
							</div>

							<div style={{ color: 'red' }}>{this.state.usernameError}</div>
							<div className='form-group'>
								<button
									type='button'
									onClick={e => this.signin()}
									className='btn btn-primary btn-block'>
									Sign in
								</button>
							</div>
							<div className='form-group'>
								<button
									type='button'
									onClick={e => this.signin()}
									className='btn btn-primary btn-block'>
									Sign up
								</button>
							</div>
						</form>
					</div>
				</div>
			</div>
		);
	}
}
export default Login;

